mapper.select:
select * from tb_hr_dimission

	and dimissionuserid = #{dimissionuserid}
	and lastworkingdate = #{lastworkingdate}
	and dimissiontype = #{dimissiontype}
	and dimissionreason = #{dimissionreason}
	and transactoruserid = #{transactoruserid}
	and transactiondate = #{transactiondate}
	and dimissiondirection = #{dimissiondirection}
	and approvalnumbers = #{approvalnumbers}
	and approvalstatue = #{approvalstatue}
	and workingnumbers = #{workingnumbers}
	and workingstatue = #{workingstatue}
	and filenumbers = #{filenumbers}
	and filestatue = #{filestatue}
	and officesupplynumbers = #{officesupplynumbers}
	and officesupplystatue = #{officesupplystatue}

mapper.update:
update tb_hr_dimission

	approvalstatue=#{approvalstatue},
	workingstatue=#{workingstatue},
	filestatue=#{filestatue},
	officesupplystatue=#{officesupplystatue}

WHERE id=#{id}
