mapper.select:
select code,cluename,trackid,resource,createtime,custom,contact,contactphone,owner,sale_employeenumber,scheme_employeenumber,state,clue_price
from tb_business_clue

	AND code=#{code}
	AND cluename=#{cluename}
	AND trackid=#{trackid}
	AND resource=#{resource}
	AND createtime=#{createtime}
	AND custom=#{custom}
	AND contact=#{contact}
	AND contactphone=#{contactphone}
	AND owner=#{owner}
	AND sale_employeenumber=#{sale_employeenumber}
	AND scheme_employeenumber=#{scheme_employeenumber}
	AND state=#{state}
	AND clue_price=#{clue_price}

		SELECT
		clue.`code`,
		clue.cluename,
		clue.trackid,
		clue.resource,
		clue.createtime,
		clue.custom,
		clue.contact,
		clue.contactphone,
		clue.`owner`,
		clue.sale_employeenumber,
		clue.scheme_employeenumber,
		clue.state,
		clue.clue_price,
		clue.participate,
		clue.track_content,
		clue.track_date,
		clue.in_department
		FROM
		tb_business_clue clue


				AND CONCAT(${queryColumn}) LIKE CONCAT(CONCAT('%',#{queryStr}),'%')

            AND clue.code=#{code}
            AND clue.cluename=#{cluename}
            AND clue.trackid=#{trackid}
            AND clue.resource=#{resource}
            AND clue.createtime=#{createtime}
            AND clue.custom=#{custom}
            AND clue.contact=#{contact}
            AND clue.contactphone=#{contactphone}
            AND clue.owner=#{owner}
            AND clue.sale_employeenumber=#{sale_employeenumber}
            AND clue.scheme_employeenumber=#{scheme_employeenumber}
            AND clue.state=#{state}
            AND clue.clue_price=#{clue_price}

SELECT
	clue.`code`,
	clue.cluename,
	clue.trackid,
	clue.resource,
	clue.createtime,
	clue.custom,
	clue.contact,
	clue.contactphone,
	clue.`owner`,
	clue.sale_employeenumber,
	clue.scheme_employeenumber,
	clue.state,
	clue.clue_price,
	clue.participate,
	clue.track_content,
	clue.track_date,
	clue.in_department
FROM
	tb_business_clue clue

clue.sale_employeenumber IN (
	SELECT
		u.employeenumber
	FROM
		tb_id_user u
	JOIN tb_id_personalinformation per ON per.userid = u.id
	JOIN tb_id_department dep ON dep.id = per.depid
	WHERE
		dep.id IN (
			SELECT
				per2.depid
			FROM
				tb_id_personalinformation per2
			JOIN tb_id_user u2 ON u2.id = per2.userid
			WHERE
				u2.username = #{username}
		)
)
AND clue.code=#{code}
AND clue.cluename=#{cluename}
AND clue.trackid=#{trackid}
AND clue.resource=#{resource}
AND clue.createtime=#{createtime}
AND clue.custom=#{custom}
AND clue.contact=#{contact}
AND clue.contactphone=#{contactphone}
AND clue.owner=#{owner}
AND clue.sale_employeenumber=#{sale_employeenumber}
AND clue.scheme_employeenumber=#{scheme_employeenumber}
AND clue.state=#{state}
AND clue.clue_price=#{clue_price}

	AND CONCAT(${queryColumn}) LIKE CONCAT(CONCAT('%',#{queryStr}),'%')

SELECT * FROM tb_business_opportunity WHERE

	code = #{code}


	clueid = #{clueid}
