mapper.select:
SELECT
	opportunity.`code`,
	opportunity.clueid,
	opportunity.opportunityname,
	opportunity.trackid,
	opportunity.resource,
	opportunity.createtime,
	opportunity.custom,
	opportunity.contact,
	opportunity.contactphone,
	opportunity.`owner`,
	opportunity.custom_decisionmaker,
	opportunity.custom_budget,
	opportunity.opportunity_budget,
	opportunity.sale_employeenumber,
	opportunity.scheme_employeenumber,
	opportunity.state,
	opportunity.opportunity_price
FROM
	tb_business_opportunity opportunity

	AND opportunity.code=#{code}
	AND opportunity.clueid=#{clueid}
	AND opportunity.opportunityname=#{opportunityname}
	AND opportunity.trackid=#{trackid}
	AND opportunity.resource=#{resource}
	AND opportunity.createtime=#{createtime}
	AND opportunity.custom=#{custom}
	AND opportunity.contact=#{contact}
	AND opportunity.contactphone=#{contactphone}
	AND opportunity.owner=#{owner}
	AND opportunity.custom_decisionmaker=#{custom_decisionmaker}
	AND opportunity.custom_budget=#{custom_budget}
	AND opportunity.opportunity_budget=#{opportunity_budget}
	AND opportunity.sale_employeenumber=#{sale_employeenumber}
	AND opportunity.scheme_employeenumber=#{scheme_employeenumber}
	AND opportunity.state=#{state}
	AND opportunity.opportunity_price=#{opportunity_price}
	#{username} in	(
		SELECT
			principaluser.username
		FROM
			tb_id_user u
			JOIN tb_id_personalinformation per ON per.userid = u.id
			JOIN tb_id_department dep ON dep.id = per.depid
			JOIN tb_id_user principaluser ON principaluser.id = dep.principaluserid
		WHERE
			u.employeenumber = opportunity.sale_employeenumber
	)
