mapper.select:
select depcode,companyname,depname,functionaltypeid,deptypeid,parent_depcode,principaluserid,deputyuserid,secretaryuserid,dutydescription,depdescription,state,ordercode,node_level
from tb_id_dep_info

	AND depcode=#{depcode}
	AND companyname=#{companyname}
	AND depname=#{depname}
	AND functionaltypeid=#{functionaltypeid}
	AND deptypeid=#{deptypeid}
	AND parent_depcode=#{parent_depcode}
	AND principaluserid=#{principaluserid}
	AND deputyuserid=#{deputyuserid}
	AND secretaryuserid=#{secretaryuserid}
	AND dutydescription=#{dutydescription}
	AND depdescription=#{depdescription}
	AND state=#{state}
	AND ordercode=#{ordercode}
	AND node_level=#{node_level}
