mapper.select:
select * from tb_id_department
where depcode = #{depcode}
ORDER BY ordercode

select * from tb_id_department

	 and depname=#{depname}
	 and depcode=#{depcode}
	 and functionaltypeid=#{functionaltypeid}
	 and dutydescription=#{dutydescription}
	 and parentdepid=#{parentdepid}
	 and principaluserid=#{principaluserid}
	 and deputyuserid=#{deputyuserid}
	 and secretaryuserid=#{secretaryuserid}
	 and deptypeid=#{deptypeid}
	and state=1

ORDER BY ordercode

mapper.update:
UPDATE tb_id_department
depname=#{depname}, depcode=#{depcode}, functionaltypeid=#{functionaltypeid}, parentdepid=#{parentdepid}, principaluserid=#{principaluserid}, principaluserid=NULL, deputyuserid=#{deputyuserid}, deputyuserid=NULL, secretaryuserid=#{secretaryuserid}, secretaryuserid=NULL, dutydescription=#{dutydescription}, depdescription=#{depdescription}, ordercode=#{ordercode}, deptypeid=#{deptypeid}, post_list=#{post_list},
WHERE id=#{id}

UPDATE tb_id_department
principaluserid=NULL , deputyuserid=NULL, secretaryuserid=NULL
WHERE id=#{id}

UPDATE tb_id_department
SET state=0
WHERE id=#{_parameter,jdbcType=NUMERIC}
