mapper.select:
select postcode,parent_postcode,postname,functionaltypeid,postfamilyid,postgradeid,postrankid,postlevelid,organization,jobdescription,duty,entryrequirements,dutyfile,state,ordercode,node_level
from tb_id_post_info

	AND postcode=#{postcode}
	AND parent_postcode=#{parent_postcode}
	AND postname=#{postname}
	AND functionaltypeid=#{functionaltypeid}
	AND postfamilyid=#{postfamilyid}
	AND postgradeid=#{postgradeid}
	AND postrankid=#{postrankid}
	AND postlevelid=#{postlevelid}
	AND organization=#{organization}
	AND jobdescription=#{jobdescription}
	AND duty=#{duty}
	AND entryrequirements=#{entryrequirements}
	AND dutyfile=#{dutyfile}
	AND state=#{state}
	AND ordercode=#{ordercode}
	AND node_level=#{node_level}

mapper.update:
UPDATE tb_id_post_info
parent_postcode=#{parent_postcode}, postname=#{postname}, functionaltypeid=#{functionaltypeid}, postfamilyid=#{postfamilyid}, postgradeid=#{postgradeid}, postrankid=#{postrankid}, postlevelid=#{postlevelid}, organization=#{organization}, jobdescription=#{jobdescription}, duty=#{duty}, entryrequirements=#{entryrequirements}, dutyfile=#{dutyfile}, state=#{state}, ordercode=#{ordercode}, node_level=#{node_level}
WHERE postcode=#{postcode}
