mapper.select:
SELECT id,datatype,datacode,datavalue FROM tb_hr_set
where id=#{id}
ORDER BY id DESC

SELECT id,datatype,datacode,datavalue FROM tb_hr_set

	AND id=#{id}
	AND datatype=#{datatype}
	AND datacode=#{datacode}
	AND datavalue=#{datavalue}

mapper.update:
UPDATE tb_hr_set
SET datavalue=#{datavalue},
    datacode=#{datacode}
WHERE id=#{id}
