mapper.select:
SELECT * FROM w_ccsqd1 WHERE (INST_STATUS_ = 'DISCARD_END' || INST_STATUS_ = 'SUCCESS_END' || INST_STATUS_ = 'RUNNING')

        AND F_SQR = #{name}


        AND F_KSCCSJ >= #{startTime}


        AND F_CCJSSJ <= #{endTime}


        AND F_XMBH = #{projectId}


        AND F_SZBM = #{department}
