mapper.select:
SELECT
	c.id,
	c.contractcode,
	c.userid,
	c.startdate,
	c.enddate,
	c.contracttypeid,
	c.attachment,
	c.remark,
	c.transactoruserid,
	c.transdate
FROM
	tb_hr_contractinformation c
LEFT JOIN tb_id_user u ON c.userid = u.id
LEFT JOIN tb_hr_set_contracttype ct ON c.contracttypeid = ct.id


		AND c.userid IN

			#{param}




			AND c.contractcode = #{contractcode}


			AND c.contractcode LIKE CONCAT(CONCAT('%',#{contractcode}),'%')


			AND c.contractcode NOT LIKE CONCAT(CONCAT('%',#{contractcode}),'%')




			AND ct.contracttype = #{contracttype}


			AND ct.contracttype LIKE CONCAT(CONCAT('%',#{contracttype}),'%')


			AND ct.contracttype NOT LIKE CONCAT(CONCAT('%',#{contracttype}),'%')




			AND c.enddate - c.startdate = #{contractage}


			 AND c.enddate - c.startdate > #{contractage} 


			 AND c.enddate - c.startdate < #{contractage} 


			 AND c.enddate - c.startdate >=  #{contractage} 


			 AND c.enddate - c.startdate <= #{contractage} 


			AND c.enddate - c.startdate = #{contractage}


			 AND c.enddate - c.startdate != #{contractage} 



		AND c.startdate BETWEEN #{startdatevalue1} AND #{startdatevalue2}


		AND c.enddate BETWEEN #{enddatevalue1} AND #{enddatevalue2}



			AND c.remark = #{remark}


			AND c.remark LIKE CONCAT(CONCAT('%',#{remark}),'%')


			AND c.remark NOT LIKE CONCAT(CONCAT('%',#{remark}),'%')


	AND c.enddate > #{curdate}

GROUP BY c.id

mapper.update:
UPDATE tb_hr_contractinformation
contractcode=#{contractcode}, userid=#{userid}, startdate=#{startdate}, enddate=#{enddate}, contracttypeid=#{contracttypeid}, attachment=#{attachment}, remark=#{remark}, transactoruserid=#{transactoruserid}, transdate=#{transdate},
WHERE id=#{id}
