mapper.select:
select dm.id, dm.dimissionuserid, dm.lastworkingdate, dm.dimissiontype, dm.dimissionreason, dm.transactoruserid, dm.transactiondate, dm.dimissiondirection from tb_hr_dimission dm JOIN tb_id_user u ON dm.transactoruserid = u.id
AND dm.dimissionuserid IN
#{param}
AND dm.lastworkingdate BETWEEN #{lastworkingdatevalue1} AND #{lastworkingdatevalue2}
AND dm.dimissiontype = #{dimissiontype}
AND dm.dimissiontype LIKE CONCAT('%',#{dimissiontype},'%')
AND dm.dimissiontype NOT LIKE CONCAT('%',#{dimissiontype},'%')
AND dm.dimissionreason = #{dimissionreason}
AND dm.dimissionreason LIKE CONCAT('%',#{dimissionreason},'%')
AND dm.dimissionreason NOT LIKE CONCAT('%',#{dimissionreason},'%')
AND dm.dimissiondirection = #{dimissiondirection}
AND dm.dimissiondirection LIKE CONCAT('%',#{dimissiondirection},'%')
AND dm.dimissiondirection NOT LIKE CONCAT('%',#{dimissiondirection},'%')
AND u.truename = #{transactortruename}
AND u.truename LIKE CONCAT('%',#{transactortruename},'%')
AND u.truename NOT LIKE CONCAT('%',#{transactortruename},'%')
AND dm.transactiondate BETWEEN #{transactiondatevalue1} AND #{transactiondatevalue2}

mapper.update:
update tb_hr_dimission
lastworkingdate=#{lastworkingdate}, dimissiontype=#{dimissiontype}, dimissionreason=#{dimissionreason}, dimissiondirection=#{dimissiondirection}, approvalstatue=#{approvalstatue}, workingstatue=#{workingstatue}, filestatue=#{filestatue}, officesupplystatue=#{officesupplystatue}
WHERE id=#{id}
