mapper.select:
SELECT per.id, per.userid, per.employeenumber, per.depid, per.sex, per.telphoneid, per.mobilephone, per.baseinformationid, per.manageinformationid, per.costinformationid, per.otherinformationid FROM tb_id_personalinformation per LEFT JOIN tb_id_baseinformation b ON per.baseinformationid = b.id LEFT JOIN tb_id_user u ON per.userid = u.id LEFT JOIN tb_id_department d ON per.depid = d.id LEFT JOIN tb_hr_per_and_post_rs r ON per.id = r.perid LEFT JOIN tb_hr_set_telphone t ON per.telphoneid = t.id
u.isactive=#{isactive}
AND u.username=#{username}
AND u.username LIKE CONCAT(CONCAT('%', #{username}), '%')
AND u.username NOT LIKE CONCAT(CONCAT('%', #{username}), '%')
AND u.truename=#{truename}
AND u.truename LIKE CONCAT(CONCAT('%', #{truename}), '%')
AND u.truename NOT LIKE CONCAT(CONCAT('%', #{truename}), '%')
AND per.employeenumber=#{employeenumber}
AND per.employeenumber LIKE CONCAT(CONCAT('%', #{employeenumber}), '%')
AND per.employeenumber NOT LIKE CONCAT(CONCAT('%', #{employeenumber}), '%')
AND per.baseinformationid IN
#{param}
AND per.sex=#{sex}
AND d.depname=#{depname}
AND d.depname LIKE CONCAT(CONCAT('%', #{depname}), '%')
AND d.depname NOT LIKE CONCAT(CONCAT('%', #{depname}), '%')
AND per.id IN
#{param}
AND t.telphone=#{telphone}
AND t.telphone LIKE CONCAT(CONCAT('%', #{telphone}), '%')
AND t.telphone NOT LIKE CONCAT(CONCAT('%', #{telphone}), '%')
AND per.mobilephone=#{mobilephone}
AND per.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}), '%')
AND per.mobilephone NOT LIKE CONCAT(CONCAT('%', #{mobilephone}), '%')
AND per.depid = #{depid}
and u.state = 1
GROUP BY per.id

SELECT per.userid FROM tb_id_personalinformation per LEFT JOIN tb_id_user u ON per.userid = u.id LEFT JOIN tb_id_department d ON per.depid = d.id LEFT JOIN tb_hr_per_and_post_rs r ON per.id = r.perid
and u.truename = #{dimissiontruename}
AND u.truename LIKE CONCAT('%',#{dimissiontruename},'%')
AND u.truename NOT LIKE CONCAT('%',#{dimissiontruename},'%')
AND per.employeenumber=#{employeenumber}
AND per.employeenumber LIKE CONCAT(CONCAT('%', #{employeenumber}), '%')
AND per.employeenumber NOT LIKE CONCAT(CONCAT('%', #{employeenumber}), '%')
AND d.depname=#{depname}
AND d.depname LIKE CONCAT(CONCAT('%', #{depname}), '%')
AND d.depname NOT LIKE CONCAT(CONCAT('%', #{depname}), '%')
AND per.id IN
#{param}
GROUP BY per.id
